mapper.select:
SELECT *
FROM (
         SELECT
             ROWNUM R, TT.*
         FROM TB_TUTORIAL TT
         WHERE TT.DELETE_YN = 'N'
           AND TITLE LIKE '%' || #{title} || '%'
           AND ROWNUM <= (#{page} + 1) * #{size}
     )
WHERE R > #{page} * #{size}

SELECT *
FROM (
         SELECT
             ROWNUM R, TT.*
         FROM TB_TUTORIAL TT
         WHERE TT.DELETE_YN = 'N'
           AND ROWNUM <= (#{page} + 1) * #{size}
     )
WHERE R > #{page} * #{size}

mapper.insert:
INSERT INTO TB_TUTORIAL (
     ID
    ,TITLE
    ,DESCRIPTION
    ,PUBLISHED
    ,INSERT_TIME
) VALUES (
     SQ_TUTORIAL.NEXTVAL
     ,#{title}
     ,#{description}
     ,'Y'
     ,TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
)

mapper.update:
UPDATE TB_TUTORIAL
SET
    TITLE = #{title}
    ,DESCRIPTION = #{description}
    ,PUBLISHED = DECODE(#{published}, 'true','Y','N')
    ,UPDATE_TIME = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
WHERE
    ID = #{id}
